function addMarker(e){var o=new google.maps.Marker({map:map,position:e,title:"Click for images"});o.addListener("click",function(){map.setZoom(10),map.setCenter(o.getPosition()),map.showImages()})}function codeAddress(){var e=$("#location-select").find("option:selected").text(),o={address:e};geocoder.geocode(o,function(e,o){o===google.maps.GeocoderStatus.OK?(map.setCenter(e[0].geometry.location),addMarker(e[0].geometry.location)):alert("Geocode was not successful for the following reason: "+o)})}function initMap(){var e={lat:-34.397,lng:150.644};geocoder=new google.maps.Geocoder,map=new google.maps.Map(document.getElementById("map-canvas"),{center:e,scrollwheel:!1,zoom:12,showImages:function(){console.log("here are the images...")}}),codeAddress()}var geocoder,map;$(document).ready(function(){initMap(),$("#location-select").change(function(){codeAddress()})});