function initMap(){var e={lat:-34.397,lng:150.644};geocoder=new google.maps.Geocoder,map=new google.maps.Map(document.getElementById("map-canvas"),{center:e,scrollwheel:!1,zoom:12,showImages:function(){console.log("here are the images...")}}),codeAddress()}function codeAddress(){var e=$("#location-select").find("option:selected").text(),o={address:e};geocoder.geocode(o,function(e,o){if(o===google.maps.GeocoderStatus.OK){map.setCenter(e[0].geometry.location);var t=new google.maps.Marker({map:map,position:e[0].geometry.location,title:"Click for images"});t.addListener("click",function(){map.setZoom(10),map.setCenter(t.getPosition()),map.showImages()})}else alert("Geocode was not successful for the following reason: "+o)})}var geocoder,map;$(document).ready(function(){initMap(),$("#location-select").change(function(){codeAddress()})});